package project.spring.webfluxplaylist.configuration;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.reactive.function.server.RequestPredicates;import org.springframework.web.reactive.function.server.RouterFunction;import org.springframework.web.reactive.function.server.RouterFunctions;import org.springframework.web.reactive.function.server.ServerResponse;import project.spring.webfluxplaylist.handler.PlaylistHandler;@Configurationpublic class RoutesConfiguration {    @Bean    public RouterFunction<ServerResponse> routerFunction(PlaylistHandler handler) {        return RouterFunctions                .route(RequestPredicates.GET("/api"), handler::findAll)                .andRoute(RequestPredicates.GET("/api/{id}"), handler::findById)                .andRoute(RequestPredicates.POST("/api"), handler::save);    }}